
DROP TABLE IF EXISTS StudentPerformance;
DROP TABLE IF EXISTS CourseGradeables;
DROP TABLE IF EXISTS StudentCourses;
DROP TABLE IF EXISTS Course;
DROP TABLE IF EXISTS Student;

CREATE TABLE Student (StudentID INTEGER PRIMARY KEY, Name TEXT);
CREATE TABLE Course (CourseID INTEGER PRIMARY KEY, Name TEXT, Professor TEXT);
CREATE TABLE StudentCourses (StudentID INTEGER, CourseID INTEGER, FOREIGN KEY (StudentID) REFERENCES Student(StudentID), FOREIGN KEY (CourseID) REFERENCES Course(CourseID), PRIMARY KEY (StudentID, CourseID));
CREATE TABLE CourseGradeables (AssignmentID INTEGER PRIMARY KEY, CourseID INTEGER, AssignmentType TEXT CHECK (AssignmentType IN ('test', 'quiz', 'essay', 'presentation', 'assignment')), DueDate DATE, FOREIGN KEY (CourseID) REFERENCES Course(CourseID));
CREATE TABLE StudentPerformance (StudentID INTEGER, AssignmentID INTEGER, TimeManagement TEXT CHECK (TimeManagement IN ('too much', 'too little', 'just enough')), Grade INTEGER, FOREIGN KEY (StudentID) REFERENCES Student(StudentID), FOREIGN KEY (AssignmentID) REFERENCES CourseGradeables(AssignmentID), PRIMARY KEY (StudentID, AssignmentID));


